<?phpclass UserController{	function actionLogin()	{		$lang = Lang::getLang(1);		include HOME.'/views/user/login.php';		return true;	}	function actionLoginScript()	{		$authData = array();		$authData = User::authData($_POST['login'],$_POST['pass']);		$auth = User::auth($authData);		if($auth == 0){			header("Location: /user/login");		}else{			header("Location: /");		}		return true;	}	function actionAccessDenied()	{		$lang = Lang::getLang(0);		include HOME.'/views/user/accessDenied.php';		return true;	}	function actionCalendar($month)	{		//User::isAuth();		$calendar = User::calendar($month);		$lang = Lang::getLang(0);		include HOME.'/views/user/calendar.php';		return true;	}	function actionLogout()	{		User::logOut();		return true;	}}?>